'use strict';

module.exports = function (_this) {
    _this.grunt.verbose.writeln('Task generateConfigFile.');
    if(_this.dest){
        _this.grunt.file.write(_this.dest, 'exports.config = ' + JSON.stringify(_this.protractorOptions, null, 4));
        _this.grunt.verbose.writeln('File "' + _this.dest + '" created.');
    } else {
        _this.grunt.warn('File "' + _this.dest + '" could not generated. Check if you set the option.dest in your config.');
    }
};
